import React from 'react';
import { Card, CardContent, Typography, Chip, Container, Grid } from '@mui/material';

const workshops = [
  {
    planningDate: "2024-13-01T09:00:00Z",
    jobs: [
      {
        startTime: "2024-21-01T10:00:00Z",
        endTime: "2024-11-01T12:00:00Z",
        description: "Oil change and tire rotation",
        status: "IN_PROGRESS",
        registrationNumber: "AZ-123",
      },
      {
        startTime: "2024-13-01T13:00:00Z",
        endTime: "2024-13-01T14:30:00Z",
        description: "Brake inspection",
        status: "PENDING",
        registrationNumber: "BZ-456",
      },
    ],
  },
  {
    planningDate: "2024-14-01T09:00:00Z",
    jobs: [
      {
        startTime: "2024-14-01T10:00:00Z",
        endTime: "2024-14-01T11:30:00Z",
        description: "Engine diagnostics",
        status: "COMPLETED",
        registrationNumber: "DZ-101",
      },
      {
        startTime: "2024-14-01T12:00:00Z",
        endTime: "2024-14-01T13:30:00Z",
        description: "Tire replacement",
        status: "IN_PROGRESS",
        registrationNumber: "EZ-202",
      },
    ],
  },
  {
    planningDate: "2024-15-01T09:00:00Z",
    jobs: [
      {
        startTime: "2024-15-01T10:00:00Z",
        endTime: "2024-15-01T11:00:00Z",
        description: "Car wash",
        status: "PENDING",
        registrationNumber: "FZ-303",
      },
    ],
  },
];

const formatDate = (isoString) => {
  const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
  return new Date(isoString).toLocaleDateString('en-US', options);
};

const statusColors = {
  IN_PROGRESS: 'warning',
  PENDING: 'default',
  COMPLETED: 'success',
};

const JobCard = ({ job }) => {
  return (
    <Card variant="outlined" style={{ marginBottom: '1rem' }}>
      <CardContent>
        <Typography variant="h6" gutterBottom>
          {job.description}
        </Typography>
        <Typography variant="body2">
          <strong>Time:</strong> {formatDate(job.startTime)} - {formatDate(job.endTime)}
        </Typography>
        <Typography variant="body2">
          <strong>Registration:</strong> {job.registrationNumber}
        </Typography>
        <Chip label={job.status} color={statusColors[job.status]} style={{ marginTop: '0.5rem' }} />
      </CardContent>
    </Card>
  );
};

const WorkshopView = ({ workshop }) => {
  return (
    <Container style={{ marginTop: '2rem' }}>
      <Typography variant="h4" gutterBottom>
        Workshop Schedule
      </Typography>
      <Typography variant="h5" gutterBottom>
        {formatDate(workshop.planningDate)}
      </Typography>
      <Grid container spacing={2}>
        {workshop.jobs.map((job, index) => (
          <Grid item xs={12} md={6} key={index}>
            <JobCard job={job} />
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

const App = () => {
  return (
    <div className="App">
      {workshops.map((workshop, index) => (
        <WorkshopView key={index} workshop={workshop} />
      ))}
    </div>
  );
};

export default App;
