import { useState, useEffect } from "react";
import endpoints from "../../config/endpoints.json";
import useAxios from "../../hooks/axios";
export default function useVehicle() {
  const columns = [
    { field: "ownerIdentityNumber", headerName: "owner Identity Number" },
    { field: "chassisNumber", headerName: "chassis Number" },
    { field: "registrationNumber", headerName: "registration Number" },
    { field: "brand", headerName: "brand" },
    {field: "model",headerName: "model"},
    {field: "yearOfManufacture",headerName: "yearOfManufacture"},
    {field: "color",headerName: "color"},
    {field: "mileage",headerName: "mileage"},
    {field: "fuelType",headerName: "fuelType"},
    {field: "dateOfPurchase",headerName: "dateOfPurchase"},
    {field: "vehicleCondition",headerName: "vehicleCondition"},
  ];
  const { customAxios } = useAxios();
  const [open, setOpen] = useState(false);
  const [openNotif, setOpenNotif] = useState(false);
  const [errors, setErrors] = useState({});
  const [formData, setFormData] = useState({
    ownerIdentityNumber: "",
    chassisNumber: "",
    registrationNumber: "",
    brand: "",
    model: "",
    yearOfManufacture: "",
    color:"",
    mileage:"",
    fuelType:"",
    dateOfPurchase:"",
    vehicleCondition:""

  });
  const [vehicles, setV] = useState([]);
  const [loading, setLoading] = useState(false);
  const [fetchError, setFetchError] = useState(null);

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const handleOpenNotif = () => setOpenNotif(true);
  const handleCloseNotif = () => setOpenNotif(false);

  const onChange = (e) => {
    const { name, value } = e.target;

    if (name.includes("address.")) {
      const addressField = name.split(".")[1];
      setFormData((prevData) => ({
        ...prevData,
        address: {
          ...prevData.address,
          [addressField]: value,
        },
      }));
    } else {
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    }

    if (errors[name]) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        [name]: undefined,
      }));
    }
  };

  const addClient = async () => {
    try {
      const result = await customAxios({
        method: "POST",
        url: endpoints.addNewClient,
        data: formData,
      });
      console.log("Client added successfully:", result.data);
      handleClose();
      handleOpenNotif();
      fetchClients();
    } catch (error) {
      console.error("Error adding client:", error.response || error.message);
    }
  };

  const fetchClients = async () => {
    setLoading(true);
    setFetchError(null);
    try {
      const response = await customAxios({
        method: "GET",
        url: endpoints.addNewClient,
      });
      setClients(response.data);
    } catch (error) {
      console.error("Error fetching clients:", error.response || error.message);
      setFetchError(error.response?.data?.message || error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchClients();
  }, []);

  return {
    onChange,
    handleOpen,
    handleClose,
    open,
    addClient,
    clients,
    loading,
    fetchError,
    columns,
    handleCloseNotif,
    handleOpenNotif,
    openNotif,
  };
}
