import {
  Button,
  Box,
  TextField,
  Typography,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";

export default function UpdateStatus({ onChange, maintenanceStatus }) {
  return (
    <Box>
      {formData.jobs.map((job, jobIndex) => (
        <div
          key={jobIndex}
          style={{
            marginBottom: "1rem",
            border: "1px solid #ddd",
            padding: "1rem",
            borderRadius: "8px",
          }}
        >
          <Typography variant="h6">Job {jobIndex + 1}</Typography>
          <Box sx={{ display: "flex", gap: "0.5rem" }}>
            <TextField
              select
              name={`jobs.${jobIndex}.status`}
              value={job.status || ""}
              onChange={onChange}
              fullWidth
              margin="normal"
              SelectProps={{ native: true }}
            >
              <option value="" disabled>
                Veuillez choisir un status
              </option>
              {maintenanceStatus.map((status) => (
                <option key={status} value={status}>
                  {status}
                </option>
              ))}
            </TextField>
            <TextField
              label="End Time"
              name={`jobs.${jobIndex}.endTime`}
              value={job.endTime}
              onChange={onChange}
              fullWidth
              margin="normal"
              type="date"
              InputLabelProps={{ shrink: true }}
            />
          </Box>
        </div>
      ))}
    </Box>
  );
}
