import { useState, useEffect } from "react";
import endpoints from "../../config/endpoints.json";
import useAxios from "../../hooks/axios";

export default funtcionWorkshop = () => {
  const [formData, setFormData] = useState([
    {
      planningDate: "",
      jobs: [
        {
          startTime: "",
          endTime: "",
          description: "",
          status: "",
          registrationNumber: "",
        },
      ],
    },
  ]);

  const onChange = (e, workshopIndex) => {
    const { name, value } = e.target;

    if (name.startsWith("jobs.")) {
      const [_, jobIndex, field] = name.split(".");
      setFormData((prevData) => {
        const updatedWorkshops = [...prevData];
        const updatedJobs = [...updatedWorkshops[workshopIndex].jobs];
        updatedJobs[jobIndex][field] = value;
        updatedWorkshops[workshopIndex] = {
          ...updatedWorkshops[workshopIndex],
          jobs: updatedJobs,
        };
        return updatedWorkshops;
      });
    } else {
      setFormData((prevData) => {
        const updatedWorkshops = [...prevData];
        updatedWorkshops[workshopIndex] = {
          ...updatedWorkshops[workshopIndex],
          [name]: value,
        };
        return updatedWorkshops;
      });
    }
  };

  const addJob = (workshopIndex) => {
    setFormData((prevData) => {
      const updatedWorkshops = [...prevData];
      updatedWorkshops[workshopIndex] = {
        ...updatedWorkshops[workshopIndex],
        jobs: [
          ...updatedWorkshops[workshopIndex].jobs,
          { startTime: "", endTime: "", description: "", status: "", registrationNumber: "" },
        ],
      };
      return updatedWorkshops;
    });
  };

  const addWorkshop = () => {
    setFormData((prevData) => [
      ...prevData,
      {
        planningDate: "",
        jobs: [
          {
            startTime: "",
            endTime: "",
            description: "",
            status: "",
            registrationNumber: "",
          },
        ],
      },
    ]);
  };



  return {
    onChange,
    handleOpen,
    handleClose,
    open,
    loading,
    fetchError,
    handleCloseNotif,
    handleOpenNotif,
    openNotif,
    formData,
    addJob,
    addWorkshop
  };
}
