import React from "react";
import { Container, Typography, Grid } from "@mui/material";

const workshops = [
  {
    planningDate: "2024-13-01T09:00:00Z",
    jobs: [
      {
        startTime: "2024-21-01T10:00:00Z",
        endTime: "2024-11-01T12:00:00Z",
        description: "Oil change and tire rotation",
        status: "IN_PROGRESS",
        registrationNumber: "AZ-123",
      },
      {
        startTime: "2024-13-01T13:00:00Z",
        endTime: "2024-13-01T14:30:00Z",
        description: "Brake inspection",
        status: "PENDING",
        registrationNumber: "BZ-456",
      },
    ],
  },
  {
    planningDate: "2024-14-01T09:00:00Z",
    jobs: [
      {
        startTime: "2024-14-01T10:00:00Z",
        endTime: "2024-14-01T11:30:00Z",
        description: "Engine diagnostics",
        status: "COMPLETED",
        registrationNumber: "DZ-101",
      },
      {
        startTime: "2024-14-01T12:00:00Z",
        endTime: "2024-14-01T13:30:00Z",
        description: "Tire replacement",
        status: "IN_PROGRESS",
        registrationNumber: "EZ-202",
      },
    ],
  },
  {
    planningDate: "2024-15-01T09:00:00Z",
    jobs: [
      {
        startTime: "2024-15-01T10:00:00Z",
        endTime: "2024-15-01T11:00:00Z",
        description: "Car wash",
        status: "PENDING",
        registrationNumber: "FZ-303",
      },
    ],
  },
];

const formatDate = (isoString) => {
  const options = {
    weekday: "long",
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "2-digit",
    minute: "2-digit",
  };
  return new Date(isoString).toLocaleDateString("en-US", options);
};

const statusColors = {
  IN_PROGRESS: "warning",
  PENDING: "default",
  COMPLETED: "success",
};

const JobCard = ({ job }) => {
  return (
    <div
      style={{
        border: "1px solid #ddd",
        padding: "1rem",
        marginBottom: "1rem",
        borderRadius: "8px",
      }}
    >
      <Typography variant="h6" gutterBottom>
        {job.description}
      </Typography>
      <Typography variant="body2">
        <strong>Time:</strong> {formatDate(job.startTime)} -{" "}
        {formatDate(job.endTime)}
      </Typography>
      <Typography variant="body2">
        <strong>Registration:</strong> {job.registrationNumber}
      </Typography>
      <div
        style={{
          marginTop: "0.5rem",
          padding: "0.25rem 0.5rem",
          backgroundColor: statusColors[job.status],
          color: "#fff",
          borderRadius: "4px",
          display: "inline-block",
        }}
      >
        {job.status}
      </div>
    </div>
  );
};

const Workshop = () => {
  return (
    <Container style={{ marginTop: "2rem" }}>
      <Typography variant="h4" gutterBottom>
        Workshop Overview
      </Typography>
      {workshops.map((workshop, index) => (
        <div key={index} style={{ marginBottom: "2rem" }}>
          <Typography variant="h5">
            {formatDate(workshop.planningDate)}
          </Typography>
          <Grid container spacing={2}>
            {workshop.jobs.map((job, jobIndex) => (
              <Grid item xs={12} md={6} key={jobIndex}>
                <JobCard job={job} />
              </Grid>
            ))}
          </Grid>
        </div>
      ))}
    </Container>
  );
};

export default Workshop;
