import { useState, useEffect } from "react";
import endpoints from "../../config/endpoints.json";
import useAxios from "../../hooks/axios";
export default function useWorkshop() {

  const { customAxios } = useAxios();
  const [open, setOpen] = useState(false);
  const [openNotif, setOpenNotif] = useState(false);
  const [errors, setErrors] = useState({});
  const [planningDate, setPlanningDate] = useState(null);
  const [jobs, setJobs] = useState([]);
  const [currentJob, setCurrentJob] = useState({});
  const [clients, setClients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [fetchError, setFetchError] = useState(null);


  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const handleOpenNotif = () => setOpenNotif(true);
  const handleCloseNotif = () => setOpenNotif(false);

  

  const addClient = async () => {
    try {
      const result = await customAxios({
        method: "POST",
        url: endpoints.addNewClient,
        data: formData,
      });
      console.log("Client added successfully:", result.data);
      handleClose();
      handleOpenNotif();
      fetchClients();
    } catch (error) {
      console.error("Error adding client:", error.response || error.message);
    }
  };

  const fetchClients = async () => {
    setLoading(true);
    setFetchError(null);
    try {
      const response = await customAxios({
        method: "GET",
        url: endpoints.addNewClient,
      });
      setClients(response.data);
    } catch (error) {
      console.error("Error fetching clients:", error.response || error.message);
      setFetchError(error.response?.data?.message || error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchClients();
  }, []);

  return {
    onChange,
    handleOpen,
    handleClose,
    open,
    addClient,
    clients,
    loading,
    fetchError,
    columns,
    handleCloseNotif,
    handleOpenNotif,
    openNotif,
  };
}
