import React from "react";
import Modal from "../../components/modal";
import Dialog from "../../components/dialogue";
import {
  Card,
  CardContent,
  Typography,
  Chip,
  Container,
  Grid,
  Box,
  Button,
} from "@mui/material";
import useWorkshop from "./service";
import MainWorkshop from "./main";

const formatDate = (isoString) => {
  const options = {
    weekday: "long",
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "2-digit",
    minute: "2-digit",
  };
  return new Date(isoString).toLocaleDateString("en-US", options);
};

const statusColors = {
  IN_PROGRESS: "warning",
  PENDING: "default",
  COMPLETED: "success",
};

const JobCard = ({ job }) => {
  return (
    <Card variant="outlined" style={{ marginBottom: "1rem" }}>
      <CardContent>
        <Typography variant="h6" gutterBottom>
          {job.description}
        </Typography>
        <Typography variant="body2">
          <strong>Time:</strong> {formatDate(job.startTime)} -{" "}
          {formatDate(job.endTime)}
        </Typography>
        <Typography variant="body2">
          <strong>Registration:</strong> {job.registrationNumber}
        </Typography>
        <Chip
          label={job.status}
          color={statusColors[job.status]}
          style={{ marginTop: "0.5rem" }}
        />
      </CardContent>
    </Card>
  );
};

export default function WorkshopView() {
  const {
    onChange,
    handleOpen,
    handleClose,
    open,
    loading,
    fetchError,
    handleCloseNotif,
    openNotif,
    formData,
    addJob,
    addWorkshop,
    maintenanceStatus,
    registrationNumber,
    workshop,
  } = useWorkshop();
  return (
    <Box>
      <Container
        sx={{
          display: "flex",
          alignItems: "center",
          margin: 0,
          justifyContent: "space-between",
          minWidth: "100%",
        }}
      >
        <Typography
          gutterBottom
          variant="h4"
          sx={{ fontWeight: 550, flexGrow: 1 }}
        >
          Workshop Schedule
        </Typography>

        <Button
          sx={{
            backgroundColor: "#f44336",
            color: "white",
            textTransform: "none",
            padding: "8px 16px",
            borderRadius: "4px",
            "&:hover": {
              backgroundColor: "#d32f2f",
            },
          }}
          onClick={handleOpen}
        >
          New Worshop +
        </Button>
        <MainWorkshop
          open={open}
          handleClose={handleClose}
          onChange={onChange}
          addWorkshop={addWorkshop}
          addJob={addJob}
          formData={formData}
          maintenanceStatus={maintenanceStatus}
          registrationNumber={registrationNumber}
        />
      </Container>
      <Container style={{ marginTop: "2rem" }}>
        <Typography variant="h5" gutterBottom>
          {formatDate(workshop.planningDate)}
        </Typography>
        <Grid container spacing={2}>
          {workshop.jobs.map((job, index) => (
            <Grid item xs={12} md={6} key={index}>
              <JobCard job={job} />
            </Grid>
          ))}
        </Grid>
      </Container>
      <Modal
        open={openNotif}
        handleClose={handleCloseNotif}
        renderContent={
          <Dialog
            message={"Workshop Added Successefuly"}
            messageButton={"Back To Workshop"}
            handleClose={handleCloseNotif}
          />
        }
        width="30%"
      />
    </Box>
  );
}
