import {
  Button,
  Box,
  TextField,
  Typography,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";

export default function UpdateStatus({
  formData,
  onChange,
  maintenanceStatus,
  updateStatus
}) {
  return (
    <Box>
      {formData.jobs.map((job, jobIndex) => (
        <div
          key={jobIndex}
          style={{
            padding: "1rem",
          }}
        >
          <Typography variant="h6">Job To Update</Typography>
          <Box sx={{ display: "flex", gap: "0.5rem" }}>
            <TextField
              select
              name={`jobs.${jobIndex}.status`}
              value={job.status}
              onChange={onChange}
              fullWidth
              margin="normal"
              SelectProps={{ native: true }}
            >
              <option value="" disabled>
                select new status
              </option>
              {maintenanceStatus.map((status) => (
                <option key={status} value={status}>
                  {status}
                </option>
              ))}
            </TextField>
            <TextField
              label="End Time"
              name={`jobs.${jobIndex}.endTime`}
              value={job.endTime}
              onChange={onChange}
              fullWidth
              margin="normal"
              type="date"
              InputLabelProps={{ shrink: true }}
            />
          </Box>
        </div>
      ))}
     <Button
          variant="contained"
          onClick={handleOpenStatus}
        >
          update Workshop
        </Button>
    </Box>
  );
}
