import React from 'react';
import './App.css'; // Assume some basic styling

const workshop = {
  planningDate: "2024-13-01T09:00:00Z",
  jobs: [
    {
      startTime: "2024-21-01T10:00:00Z",
      endTime: "2024-11-01T12:00:00Z",
      description: "Oil change and tire rotation",
      status: "IN_PROGRESS",
      registrationNumber: "AZ-123",
    },
    {
      startTime: "2024-13-01T13:00:00Z",
      endTime: "2024-13-01T14:30:00Z",
      description: "Brake inspection",
      status: "PENDING",
      registrationNumber: "BZ-456",
    },
    {
      startTime: "2024-13-01T15:00:00Z",
      endTime: "2024-13-01T16:30:00Z",
      description: "Battery replacement",
      status: "COMPLETED",
      registrationNumber: "CZ-789",
    },
  ],
};

const formatDate = (isoString) => {
  const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
  return new Date(isoString).toLocaleDateString('en-US', options);
};

const JobCard = ({ job }) => {
  const statusColors = {
    IN_PROGRESS: 'orange',
    PENDING: 'gray',
    COMPLETED: 'green',
  };

  return (
    <div className="job-card">
      <h3>{job.description}</h3>
      <p><strong>Time:</strong> {formatDate(job.startTime)} - {formatDate(job.endTime)}</p>
      <p><strong>Registration:</strong> {job.registrationNumber}</p>
      <p>
        <strong>Status:</strong>{' '}
        <span style={{ color: statusColors[job.status] }}>{job.status}</span>
      </p>
    </div>
  );
};

const WorkshopView = () => {
  return (
    <div className="workshop-view">
      <h1>Workshop Schedule</h1>
      <h2>{formatDate(workshop.planningDate)}</h2>
      <div className="jobs-container">
        {workshop.jobs.map((job, index) => (
          <JobCard key={index} job={job} />
        ))}
      </div>
    </div>
  );
};

const App = () => {
  return (
    <div className="App">
      <WorkshopView />
    </div>
  );
};

export default App;
