import { useState, useEffect } from "react";
import endpoints from "../../config/endpoints.json";
import useAxios from "../../hooks/axios";

export default function useWorkshop() {
  const { customAxios } = useAxios();
  const [formData, setFormData] = useState({
    planningDate: "",
    jobs: [
      {
        startTime: "",
        endTime: "",
        description: "",
        status: "",
        registrationNumber: "",
      },
    ],
  });

  const [maintenanceStatus, setMaintenanceStatus] = useState([]);

  // Handle changes in form data
  const onChange = (e) => {
    const { name, value } = e.target;

    if (name.startsWith("jobs.")) {
      const [_, jobIndex, field] = name.split(".");
      setFormData((prevData) => {
        const updatedJobs = [...prevData.jobs];
        updatedJobs[jobIndex][field] = value;
        return { ...prevData, jobs: updatedJobs };
      });
    } else {
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    }
  };

  // Add a new job to the form
  const addJob = () => {
    setFormData((prevData) => ({
      ...prevData,
      jobs: [
        ...prevData.jobs,
        {
          startTime: "",
          endTime: "",
          description: "",
          status: "",
          registrationNumber: "",
        },
      ],
    }));
  };

  // Fetch the maintenance statuses from API
  const fetchMaintenanceStatus = async () => {
    try {
      const response = await customAxios({
        method: "GET",
        url: endpoints.maintenanceStatus,
      });
      setMaintenanceStatus(response.data);
    } catch (error) {
      console.error("Error fetching maintenance status:", error);
    }
  };

  // Save workshop (submit the form)
  const saveWorkshop = async () => {
    try {
      const response = await customAxios({
        method: "POST",
        url: endpoints.saveWorkshop,
        data: formData,
      });
      console.log("Workshop saved successfully:", response.data);
    } catch (error) {
      console.error("Error saving workshop:", error);
    }
  };

  useEffect(() => {
    fetchMaintenanceStatus();
  }, []);

  return {
    onChange,
    formData,
    addJob,
    maintenanceStatus,
    saveWorkshop,
  };
}
