import { useState } from "react";
import endpoints from "../../config/endpoints.json";
import useAxios from "../../hooks/axios";
export default function useClient() {
  const { customAxios } = useAxios();

  const [open, setOpen] = useState(false);
  const [errors, setErrors] = useState({});
  const [formData, setFormData] = useState({
    identityNumber: "",
    firstName: "",
    lastName: "",
    phoneNumber: "",
    email: "",
    address: {
      country: "",
      city: "",
      street: "",
      buildingNumber: "",
    },
  });

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const onChange = (e) => {
    const { name, value } = e.target;

    if (name.includes("address.")) {
      // Handle nested object updates (e.g., address.country)
      const addressField = name.split(".")[1];
      setFormData((prevData) => ({
        ...prevData,
        address: {
          ...prevData.address,
          [addressField]: value,
        },
      }));
    } else {
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    }

    // Clear errors for the field
    if (errors[name]) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        [name]: undefined,
      }));
    }
  };

  const addClient = async () => {
    try {
      const result = await customAxios({
        method: "POST",
        url: endpoints.addNewClient,
        data: formData,
      });
      console.log("Client added successfully:", result.data);
      handleClose();
    } catch (error) {
      console.error("Error adding client:", error.response || error.message);
    }
  };

  return { onChange, handleOpen, handleClose, open, addClient };
}
