import { useState, useEffect } from "react";
import endpoints from "../../config/endpoints.json";
import useAxios from "../../hooks/axios";

export default function useWorkshop() {
  const { customAxios } = useAxios();
  const [open, setOpen] = useState(false);
  const [openNotif, setOpenNotif] = useState(false);
  const [errors, setErrors] = useState({});

  const [loading, setLoading] = useState(false);
  const [fetchError, setFetchError] = useState(null);

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const handleOpenNotif = () => setOpenNotif(true);
  const handleCloseNotif = () => setOpenNotif(false);

  const [maintenanceStatus, setMaintenanceStatus] = useState([]);
  const [selectedMaintenanceStatus, setSelectedMaintenanceStatus] =
    useState("");
  const [registrationNumber, setRegistrationNumber] = useState([]);
  const [selectedRegistrationNumber, setSelectedRegistrationNumber] =
    useState("");
  const [formData, setFormData] = useState({
    planningDate: "",
    jobs: [
      {
        startTime: "",
        endTime: "",
        description: "",
        status: "",
        registrationNumber: "",
      },
    ],
  });

  const onChange = (e, workshopIndex) => {
    const { name, value } = e.target;
    if (name === "status") {
      setSelectedMaintenanceStatus(value);
    }

    if (name.startsWith("jobs.")) {
      const [_, jobIndex, field] = name.split(".");
      setFormData((prevData) => {
        const updatedJobs = [...prevData.jobs]; // Copy the jobs array
        updatedJobs[jobIndex][field] = value; // Update the specific job
        return { ...prevData, jobs: updatedJobs }; // Update the formData with new jobs array
      });
    } else {
      // Update other fields in formData (like planningDate)
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    }
  };

  const addJob = () => {
    setFormData((prevData) => ({
      ...prevData,
      jobs: [
        ...prevData.jobs,
        {
          startTime: "",
          endTime: "",
          description: "",
          status: "",
          registrationNumber: "",
        },
      ],
    }));
  };
  const fetchMaintenanceStatus = async () => {
    try {
      const response = await customAxios({
        method: "GET",
        url: endpoints.maintenanceStatus,
      });
      setMaintenanceStatus(response.data);
      console.log(maintenanceStatus);
    } catch (error) {
      console.error(
        "Error fetching maintenance status:",
        error.response || error.message
      );
    }
  };
  const fetchRegistrationsNumbers = async () => {
    try {
      const response = await customAxios({
        method: "GET",
        url: endpoints.registrationsNumbers,
      });
      setRegistrationNumber(response.data);
    } catch (error) {
      console.error("Error fetching  :", error.response || error.message);
    }
  };
  const addWorkshop = () => {
    setFormData((prevData) => ({
      planningDate: "",
      jobs: [
        {
          startTime: "",
          endTime: "",
          description: "",
          status: "",
          registrationNumber: "",
        },
      ],
    }));
  };
  useEffect(() => {
    fetchMaintenanceStatus();
    fetchRegistrationsNumbers();
  }, []);

  return {
    onChange,
    handleOpen,
    handleClose,
    open,
    loading,
    fetchError,
    handleCloseNotif,
    handleOpenNotif,
    openNotif,
    formData,
    addJob,
    addWorkshop,
    selectedMaintenanceStatus,
    maintenanceStatus,
  };
}
