import React from "react";
import {
  Card,
  CardContent,
  Typography,
  Chip,
  Container,
  Grid,
} from "@mui/material";
import useWorkshop from "./service"

const workshop = {
  planningDate: "2024-13-01T09:00:00Z",
  jobs: [
    {
      startTime: "2024-21-01T10:00:00Z",
      endTime: "2024-11-01T12:00:00Z",
      description: "Oil change and tire rotation",
      status: "IN_PROGRESS",
      registrationNumber: "AZ-123",
    },
    {
      startTime: "2024-13-01T13:00:00Z",
      endTime: "2024-13-01T14:30:00Z",
      description: "Brake inspection",
      status: "PENDING",
      registrationNumber: "BZ-456",
    },
    {
      startTime: "2024-13-01T15:00:00Z",
      endTime: "2024-13-01T16:30:00Z",
      description: "Battery replacement",
      status: "COMPLETED",
      registrationNumber: "CZ-789",
    },
  ],
};

const formatDate = (isoString) => {
  const options = {
    weekday: "long",
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "2-digit",
    minute: "2-digit",
  };
  return new Date(isoString).toLocaleDateString("en-US", options);
};

const statusColors = {
  IN_PROGRESS: "warning",
  PENDING: "default",
  COMPLETED: "success",
};

const JobCard = ({ job }) => {
  return (
    <Card variant="outlined" style={{ marginBottom: "1rem" }}>
      <CardContent>
        <Typography variant="h6" gutterBottom>
          {job.description}
        </Typography>
        <Typography variant="body2">
          <strong>Time:</strong> {formatDate(job.startTime)} -{" "}
          {formatDate(job.endTime)}
        </Typography>
        <Typography variant="body2">
          <strong>Registration:</strong> {job.registrationNumber}
        </Typography>
        <Chip
          label={job.status}
          color={statusColors[job.status]}
          style={{ marginTop: "0.5rem" }}
        />
      </CardContent>
    </Card>
  );
};

export default function WorkshopView() {
    const {
        fetchError,
        clients,
        handleOpen,
        open,
        handleClose,
        onChange,
        addClient,
        columns,
        handleCloseNotif,
        handleOpenNotif,
        openNotif,
      } = use();
  return (
    <Box>
      <Container
        sx={{
          display: "flex",
          alignItems: "center",
          margin: 0,
          justifyContent: "space-between",
          minWidth: "100%",
        }}
      >
        <Typography variant="h4" sx={{ fontWeight: 550, flexGrow: 1 }}>
          Clients
        </Typography>
        <Button
          sx={{
            backgroundColor: "#f44336",
            color: "white",
            textTransform: "none",
            padding: "8px 16px",
            borderRadius: "4px",
            "&:hover": {
              backgroundColor: "#d32f2f",
            },
          }}
          onClick={handleOpen}
        >
          New Client +
        </Button>
        <ClientAdd
          open={open}
          handleClose={handleClose}
          onChange={onChange}
          addClient={addClient}
        />
      </Container>
      <Container style={{ marginTop: "2rem" }}>
        <Typography variant="h4" gutterBottom>
          Workshop Schedule
        </Typography>
        <Typography variant="h5" gutterBottom>
          {formatDate(workshop.planningDate)}
        </Typography>
        <Grid container spacing={2}>
          {workshop.jobs.map((job, index) => (
            <Grid item xs={12} md={6} key={index}>
              <JobCard job={job} />
            </Grid>
          ))}
        </Grid>
      </Container>
    </Box>
  );
}
