import React, { useState } from "react";
import { Modal, Button, Form, Input, DatePicker, TimePicker } from "antd";

const PlanningForm = () => {
  const [planningDate, setPlanningDate] = useState(null);
  const [jobs, setJobs] = useState([]);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [currentJob, setCurrentJob] = useState({});

  const handleAddJob = () => {
    setIsModalVisible(true);
  };

  const handleModalOk = () => {
    setJobs([...jobs, currentJob]);
    setCurrentJob({}); // Reset the job form
    setIsModalVisible(false);
  };

  const handleModalCancel = () => {
    setCurrentJob({});
    setIsModalVisible(false);
  };

  const handleJobChange = (key, value) => {
    setCurrentJob({ ...currentJob, [key]: value });
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Planning Form</h2>
      <Form layout="vertical">
        <Form.Item label="Planning Date">
          <DatePicker
            onChange={(date) => setPlanningDate(date?.toISOString())}
          />
        </Form.Item>
        <Form.Item>
          <Button type="primary" onClick={handleAddJob}>
            Add Job
          </Button>
        </Form.Item>
      </Form>

      {/* Modal for adding jobs */}
      <Modal
        title="Add Job"
        visible={isModalVisible}
        onOk={handleModalOk}
        onCancel={handleModalCancel}
      >
        <Form layout="vertical">
          <Form.Item label="Start Time">
            <TimePicker
              use12Hours
              format="h:mm A"
              onChange={(time) =>
                handleJobChange("startTime", time?.toISOString())
              }
            />
          </Form.Item>
          <Form.Item label="End Time">
            <TimePicker
              use12Hours
              format="h:mm A"
              onChange={(time) =>
                handleJobChange("endTime", time?.toISOString())
              }
            />
          </Form.Item>
          <Form.Item label="Description">
            <Input
              onChange={(e) => handleJobChange("description", e.target.value)}
            />
          </Form.Item>
          <Form.Item label="Status">
            <Input
              onChange={(e) => handleJobChange("status", e.target.value)}
            />
          </Form.Item>
          <Form.Item label="Registration Number">
            <Input
              onChange={(e) =>
                handleJobChange("registrationNumber", e.target.value)
              }
            />
          </Form.Item>
        </Form>
      </Modal>

      {/* Display the current jobs */}
      <div style={{ marginTop: "20px" }}>
        <h3>Jobs List</h3>
        <ul>
          {jobs.map((job, index) => (
            <li key={index}>
              <strong>{job.description}</strong> - {job.startTime} to {job.endTime}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default PlanningForm;
