import { Box, TextField, Typography, Button } from "@mui/material";

export default function DynamicForm({ config, AddClient, onChange }) {
  const renderFields = (fields) => {
    return fields.map((field) => {
      if (field.type === "group") {
        return (
          <Box key={field.id} sx={{ mt: 2 }}>
            <Typography variant="h6">{field.label}</Typography>
            <Box sx={{ display: "flex", gap: 2, flexWrap: "wrap" }}>
              {renderFields(field.fields)}
            </Box>
          </Box>
        );
      }

      return (
        <TextField
          key={field.id}
          margin="normal"
          fullWidth
          id={field.id}
          label={field.label}
          type={field.type}
          required={field.required}
          onChange={(e) =>
            onChange(
              field.id,
              field.type === "number" ? +e.target.value : e.target.value
            )
          }
        />
      );
    });
  };

  return (
    <Box sx={{ mx: 4 }}>
      <Typography component="h1" variant="h5" sx={{ mb: 3 }}>
        Dynamic Form
      </Typography>
      <form noValidate>
        {renderFields(config)}
        <Button
          type="submit"
          variant="contained"
          sx={{ mt: 3 }}
          onClick={(e) => {
            e.preventDefault();
            add();
          }}
        >
          Submit
        </Button>
      </form>
    </Box>
  );
}
