import React, { useState } from 'react';
import { Container, Typography, Grid, TextField, Button ,Box } from '@mui/material';

const Workshop = ({onChange,formData,addJob}) => {
  return (
    <Container style={{ marginTop: '2rem' }}>
      <Typography variant="h4" gutterBottom>
        Workshop Overview
      </Typography>
      <TextField
        label="Planning Date"
        name="planningDate"
        value={formData.planningDate}
        onChange={onChange}
        fullWidth
        margin="normal"
      />
      {formData.jobs.map((job, jobIndex) => (
        <div key={jobIndex} style={{ marginBottom: '1rem', border: '1px solid #ddd', padding: '1rem', borderRadius: '8px' }}>
          <Typography variant="h6">Job {jobIndex + 1}</Typography>
          <Box sx={{ display: "flex", gap: "0.5rem" }}>
          <TextField
            label="Start Time"
            name={`jobs.${jobIndex}.startTime`}
            value={job.startTime}
            onChange={onChange}
            fullWidth
            margin="normal"
          />
          <TextField
            label="End Time"
            name={`jobs.${jobIndex}.endTime`}
            value={job.endTime}
            onChange={onChange}
            fullWidth
            margin="normal"
          />
          </Box>
          <Box sx={{ display: "flex", gap: "0.5rem" }}>

          </Box>
          <TextField
            label="Description"
            name={`jobs.${jobIndex}.description`}
            value={job.description}
            onChange={onChange}
            fullWidth
            margin="normal"
          />
         
        </div>
      ))}
      <Button variant="contained" color="primary" onClick={addJob}>
        Add Job
      </Button>
    </Container>
  );
};

export default Workshop;
