import { Box, TextField, Typography, Button, Grid } from "@mui/material";

export default function DynamicForm({ config, onSubmit, onChange }) {
  const renderFields = (fields) => {
    return fields.map((field) => {
      if (field.type === "group") {
        return (
          <Box key={field.id} sx={{ mt: 2 }}>
            <Typography variant="h6" sx={{ mb: 1 }}>
              {field.label}
            </Typography>
            <Grid container spacing={2}>
              {renderFields(field.fields)}
            </Grid>
          </Box>
        );
      }

      return (
        <Grid item xs={12} sm={6} key={field.id}>
          <TextField
            fullWidth
            id={field.id}
            label={field.label}
            type={field.type}
            required={field.required}
            onChange={(e) =>
              onChange(field.id, field.type === "number" ? +e.target.value : e.target.value)
            }
          />
        </Grid>
      );
    });
  };

  return (
    <Box sx={{ mx: 4 }}>
      <Typography component="h1" variant="h5" sx={{ mb: 3 }}>
        Dynamic Form
      </Typography>
      <form noValidate>
        <Grid container spacing={2}>
          {renderFields(config)}
        </Grid>
        <Button
          type="submit"
          variant="contained"
          sx={{ mt: 3 }}
          onClick={(e) => {
            e.preventDefault();
            onSubmit();
          }}
        >
          Submit
        </Button>
      </form>
    </Box>
  );
}
