import { createTheme } from "@mui/material/styles";

 const theme = createTheme({
  palette: {
    primary: {
      main: "#f44336", // Couleur principale (bleu)
    },
    secondary: {
      main: "#ff4081", // Couleur secondaire (rose)
    },
  },
  typography: {
    fontFamily: "Roboto, Arial, sans-serif",
    h1: {
      fontSize: "2.5rem",
      fontWeight: 700,
    },
    body1: {
      fontSize: "1rem",
    },
  },
});
export default theme;
